import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import './Navbar.css';

const Navbar = () => {
  const { user, logout, isAuthenticated, isFarmer, isBuyer } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <nav className="navbar">
      <div className="container">
        <div className="navbar-content">
          <Link to="/" className="navbar-brand">
            ðŸŒ¾ Farm Marketplace
          </Link>

          <div className="navbar-menu">
            {!isAuthenticated ? (
              <>
                <Link to="/" className="nav-link">Home</Link>
                <Link to="/products" className="nav-link">Products</Link>
                <Link to="/login" className="nav-link">Login</Link>
                <Link to="/signup" className="btn btn-primary">Sign Up</Link>
              </>
            ) : (
              <>
                {isFarmer && (
                  <>
                    <Link to="/farmer/dashboard" className="nav-link">Dashboard</Link>
                    <Link to="/farmer/products" className="nav-link">My Products</Link>
                    <Link to="/farmer/orders" className="nav-link">Orders</Link>
                  </>
                )}

                {isBuyer && (
                  <>
                    <Link to="/buyer/dashboard" className="nav-link">Dashboard</Link>
                    <Link to="/products" className="nav-link">Browse Products</Link>
                    <Link to="/buyer/orders" className="nav-link">My Orders</Link>
                  </>
                )}

                <div className="user-menu">
                  <span className="user-name">ðŸ‘¤ {user?.name}</span>
                  <button onClick={handleLogout} className="btn btn-secondary btn-sm">
                    Logout
                  </button>
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
