import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { orderApi } from '../../api/orderApi';
import Loading from '../../components/common/Loading';
import ErrorMessage from '../../components/common/ErrorMessage';
import { formatCurrency, formatDate } from '../../utils/helpers';
import { ORDER_STATUS_COLORS, ORDER_STATUS_LABELS } from '../../utils/constants';

const OrderDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [actionLoading, setActionLoading] = useState(false);

  useEffect(() => {
    fetchOrder();
  }, [id]);

  const fetchOrder = async () => {
    try {
      const response = await orderApi.getBuyerOrderById(id);
      setOrder(response.data);
    } catch (err) {
      setError('Failed to load order details');
    } finally {
      setLoading(false);
    }
  };

  const handleCancelOrder = async () => {
    if (!window.confirm('Are you sure you want to cancel this order?')) return;

    setActionLoading(true);
    try {
      await orderApi.cancelOrder(id);
      alert('Order cancelled successfully');
      fetchOrder();
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to cancel order');
    } finally {
      setActionLoading(false);
    }
  };

  if (loading) return <Loading />;
  if (!order) return <ErrorMessage message="Order not found" />;

  return (
    <div className="container">
      <button onClick={() => navigate('/buyer/orders')} className="btn btn-secondary mb-2">
        ‚Üê Back to Orders
      </button>

      <div className="card">
        <div className="flex-between mb-3">
          <h2>Order #{order.id}</h2>
          <span className={`badge badge-${ORDER_STATUS_COLORS[order.status]}`}>
            {ORDER_STATUS_LABELS[order.status]}
          </span>
        </div>

        <ErrorMessage message={error} onClose={() => setError('')} />

        <div className="order-details-section">
          <h3>Order Information</h3>
          <p><strong>Order Date:</strong> {formatDate(order.createdAt)}</p>
          <p><strong>Status:</strong> {ORDER_STATUS_LABELS[order.status]}</p>
        </div>

        <div className="order-details-section">
          <h3>Farmer Details</h3>
          <p><strong>Name:</strong> {order.farmerName}</p>
        </div>

        <div className="order-details-section">
          <h3>Items</h3>
          <table className="order-items-table">
            <thead>
              <tr>
                <th>Product</th>
                <th>Quantity</th>
                <th>Price</th>
                <th>Subtotal</th>
              </tr>
            </thead>
            <tbody>
              {order.items.map(item => (
                <tr key={item.id}>
                  <td>{item.productName}</td>
                  <td>{item.quantity}</td>
                  <td>{formatCurrency(item.priceEach)}</td>
                  <td>{formatCurrency(item.subtotal)}</td>
                </tr>
              ))}
            </tbody>
            <tfoot>
              <tr>
                <td colSpan="3"><strong>Total</strong></td>
                <td><strong>{formatCurrency(order.totalAmount)}</strong></td>
              </tr>
            </tfoot>
          </table>
        </div>

        {order.status === 'PENDING' && (
          <div className="order-actions mt-3">
            <button 
              onClick={handleCancelOrder} 
              className="btn btn-danger"
              disabled={actionLoading}
            >
              {actionLoading ? 'Cancelling...' : 'Cancel Order'}
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default OrderDetails;
