import React, { useState, useEffect, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { orderApi } from '../../api/orderApi';
import Loading from '../../components/common/Loading';
import { formatCurrency, formatDate } from '../../utils/helpers';
import { ORDER_STATUS_LABELS, ORDER_STATUS_COLORS } from '../../utils/constants';

const OrderDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchOrder = useCallback(async () => {
    try {
      setLoading(true);
      setError('');

      console.log('üöÄ Starting fetchOrder...');
      console.log('üìû Calling: orderApi.getFarmerOrderById(' + id + ')');
      console.log('üîê Token:', localStorage.getItem('token')?.substring(0, 20) + '...');
      console.log('üë§ User:', localStorage.getItem('user'));

      // Make the API call
      const response = await orderApi.getOrderById(id);

      console.log('‚úÖ SUCCESS! Response received:');
      console.log('üì¶ Response status:', response.status);
      console.log('üì¶ Response data:', response.data);
      console.log('üì¶ Response headers:', response.headers);

      setOrder(response.data);
      console.log('‚úÖ Order state updated successfully');

    } catch (err) {
      console.error('‚ùå ERROR CAUGHT!');
      console.error('‚ùå Error object:', err);
      console.error('‚ùå Error message:', err.message);
      console.error('‚ùå Error response:', err.response);
      console.error('‚ùå Response status:', err.response?.status);
      console.error('‚ùå Response data:', err.response?.data);
      console.error('‚ùå Response headers:', err.response?.headers);
      console.error('‚ùå Request config:', err.config);

      let errorMessage = 'Failed to load order details';

      if (err.response) {
        // Server responded with error
        console.error('üî¥ Server Error Response');
        if (err.response.status === 404) {
          errorMessage = 'Order not found';
        } else if (err.response.status === 403) {
          errorMessage = 'You do not have permission to view this order';
        } else if (err.response.status === 401) {
          errorMessage = 'Unauthorized. Please login again.';
        } else if (err.response.data?.message) {
          errorMessage = err.response.data.message;
        }
      } else if (err.request) {
        // Request made but no response
        console.error('üî¥ No Response Received');
        errorMessage = 'No response from server. Please check your connection.';
      } else {
        // Something else happened
        console.error('üî¥ Request Setup Error');
        errorMessage = err.message || 'An error occurred';
      }

      setError(errorMessage);
    } finally {
      setLoading(false);
      console.log('üèÅ fetchOrder completed');
    }
  }, [id]); // Add id as dependency since it's used inside the function

  useEffect(() => {
    console.log('='.repeat(50));
    console.log('üî∑ OrderDetails Component Mounted');
    console.log('üìã Order ID from params:', id);
    console.log('üìã Type of ID:', typeof id);
    console.log('='.repeat(50));
    fetchOrder();
  }, [id, fetchOrder]); // Now fetchOrder is properly included in dependencies

  const handleConfirm = async () => {
    if (!window.confirm('Are you sure you want to confirm this order?')) return;
    try {
      console.log('üì§ Confirming order:', id);
      await orderApi.confirmOrder(id);
      await fetchOrder();
    } catch (err) {
      console.error('‚ùå Confirm error:', err);
      alert(err.response?.data?.message || 'Failed to confirm order');
    }
  };

  const handleReject = async () => {
    if (!window.confirm('Are you sure you want to reject this order?')) return;
    try {
      console.log('üì§ Rejecting order:', id);
      await orderApi.rejectOrder(id);
      await fetchOrder();
    } catch (err) {
      console.error('‚ùå Reject error:', err);
      alert(err.response?.data?.message || 'Failed to reject order');
    }
  };

  const handleComplete = async () => {
    if (!window.confirm('Are you sure you want to complete this order?')) return;
    try {
      console.log('üì§ Completing order:', id);
      await orderApi.completeOrder(id);
      await fetchOrder();
    } catch (err) {
      console.error('‚ùå Complete error:', err);
      alert(err.response?.data?.message || 'Failed to complete order');
    }
  };

  console.log('üé® Rendering OrderDetails...');
  console.log('üìä Current state - loading:', loading, 'error:', error, 'order:', order);

  if (loading) {
    console.log('‚è≥ Showing loading state');
    return <Loading />;
  }

  if (error) {
    console.log('‚ö†Ô∏è Showing error state:', error);
    return (
      <div className="container">
        <button onClick={() => navigate('/farmer/orders')} className="btn btn-secondary mb-2">
          ‚Üê Back to Orders
        </button>
        <div className="error-message">
          <strong>Error:</strong> {error}
          <br />
          <small>Order ID: {id}</small>
        </div>
      </div>
    );
  }

  if (!order) {
    console.log('‚ö†Ô∏è No order data');
    return (
      <div className="container">
        <button onClick={() => navigate('/farmer/orders')} className="btn btn-secondary mb-2">
          ‚Üê Back to Orders
        </button>
        <p>Order not found</p>
      </div>
    );
  }

  console.log('‚úÖ Rendering order details');

  return (
    <div className="container">
      <button onClick={() => navigate('/farmer/orders')} className="btn btn-secondary mb-2">
        ‚Üê Back
      </button>

      <div className="card">
        <div className="order-header">
          <div className="flex-between">
            <h2>Order #{order.id}</h2>
            <span className={`badge badge-${ORDER_STATUS_COLORS[order.status]}`}>
              {ORDER_STATUS_LABELS[order.status]}
            </span>
          </div>
        </div>

        <div className="order-meta">
          <div className="meta-item">
            <span className="meta-label">Order Date</span>
            <span className="meta-value">{formatDate(order.createdAt)}</span>
          </div>
          <div className="meta-item">
            <span className="meta-label">Order Status</span>
            <span className="meta-value">{ORDER_STATUS_LABELS[order.status]}</span>
          </div>
          <div className="meta-item">
            <span className="meta-label">Total Amount</span>
            <span className="meta-value">{formatCurrency(order.totalAmount)}</span>
          </div>
        </div>

        <div className="buyer-info">
          <h3>Buyer Details</h3>
          <div className="order-meta" style={{ background: 'white', marginTop: '15px' }}>
            <div className="meta-item">
              <span className="meta-label">Name</span>
              <span className="meta-value">{order.buyerName || "Not Provided"}</span>
            </div>
            <div className="meta-item">
              <span className="meta-label">Email</span>
              <span className="meta-value">{order.buyerEmail || "Not Provided"}</span>
            </div>
            <div className="meta-item">
              <span className="meta-label">Phone</span>
              <span className="meta-value">{order.buyerPhone || "Not Provided"}</span>
            </div>
            <div className="meta-item" style={{ gridColumn: '1 / -1' }}>
              <span className="meta-label">Address</span>
              <span className="meta-value">{order.buyerAddress || "Not Provided"}</span>
            </div>
          </div>
        </div>

        <div className="order-items">
          <h3>Order Items</h3>
          <table className="items-table">
            <thead>
              <tr>
                <th>Product</th>
                <th>Quantity</th>
                <th>Price</th>
                <th>Subtotal</th>
              </tr>
            </thead>
            <tbody>
              {order.items?.map(item => (
                <tr key={item.id}>
                  <td>{item.productName}</td>
                  <td>{item.quantity}</td>
                  <td>{formatCurrency(item.priceEach)}</td>
                  <td>{formatCurrency(item.subtotal)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        <div className="order-total">
          <h3>Total: {formatCurrency(order.totalAmount)}</h3>
        </div>

        <div className="order-actions">
          {order.status === 'PENDING' && (
            <>
              <button onClick={handleConfirm} className="btn btn-success">
                ‚úì Confirm Order
              </button>
              <button onClick={handleReject} className="btn btn-danger">
                ‚úó Reject Order
              </button>
            </>
          )}
          {order.status === 'CONFIRMED' && (
            <button onClick={handleComplete} className="btn btn-primary">
              ‚úì Mark as Completed
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default OrderDetails;
