import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { orderApi } from '../../api/orderApi';
import Loading from '../../components/common/Loading';
import { formatCurrency, formatDate } from '../../utils/helpers';
import { ORDER_STATUS_LABELS, ORDER_STATUS_COLORS } from '../../utils/constants';

const OrderDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [order, setOrder] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchOrder = async () => {
    try {
      console.log('=== ORDER DETAILS DEBUG ===');
      console.log('1. Order ID from URL:', id);
      console.log('2. Calling API endpoint: /farmer/orders/' + id);

      const token = localStorage.getItem('token');
      console.log('3. JWT Token exists:', !!token);
      console.log('4. Token preview:', token ? token.substring(0, 20) + '...' : 'NO TOKEN');

      const response = await orderApi.getFarmerOrderById(id);

      console.log('5. ✅ API Response received successfully!');
      console.log('6. Response data:', response.data);
      console.log('7. Buyer info present:', {
        buyerId: response.data.buyerId,
        buyerName: response.data.buyerName,
        buyerEmail: response.data.buyerEmail,
        buyerPhone: response.data.buyerPhone,
        buyerAddress: response.data.buyerAddress
      });

      setOrder(response.data);
      setError('');
    } catch (err) {
      console.error('❌ ERROR fetching order:');
      console.error('Error object:', err);
      console.error('Error message:', err.message);
      console.error('Response status:', err.response?.status);
      console.error('Response data:', err.response?.data);
      console.error('Request config:', err.config);

      setError(err.response?.data?.message || err.message || 'Failed to load order details');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    console.log('OrderDetails component mounted, fetching order...');
    fetchOrder();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [id]);

  const handleConfirm = async () => {
    if (!window.confirm('Are you sure you want to confirm this order?')) return;

    try {
      await orderApi.confirmOrder(id);
      console.log('Order confirmed successfully');
      fetchOrder();
    } catch (err) {
      console.error('Error confirming order:', err);
      alert('Failed to confirm order');
    }
  };

  const handleReject = async () => {
    if (!window.confirm('Are you sure you want to reject this order?')) return;

    try {
      await orderApi.rejectOrder(id);
      console.log('Order rejected successfully');
      fetchOrder();
    } catch (err) {
      console.error('Error rejecting order:', err);
      alert('Failed to reject order');
    }
  };

  const handleComplete = async () => {
    if (!window.confirm('Are you sure you want to complete this order?')) return;

    try {
      await orderApi.completeOrder(id);
      console.log('Order completed successfully');
      fetchOrder();
    } catch (err) {
      console.error('Error completing order:', err);
      alert('Failed to complete order');
    }
  };

  if (loading) {
    console.log('Rendering loading state...');
    return <Loading />;
  }

  if (error) {
    console.log('Rendering error state:', error);
    return (
      <div className="container">
        <div className="error-message">{error}</div>
        <button onClick={() => navigate(-1)} className="btn btn-secondary" style={{ marginTop: '20px' }}>
          ← Go Back
        </button>
        <button onClick={fetchOrder} className="btn btn-primary" style={{ marginTop: '20px', marginLeft: '10px' }}>
          Try Again
        </button>
      </div>
    );
  }

  if (!order) {
    console.log('Order data is null/undefined');
    return (
      <div className="container">
        <p>Order not found</p>
        <button onClick={() => navigate(-1)} className="btn btn-secondary">
          ← Go Back
        </button>
      </div>
    );
  }

  console.log('Rendering order details for order:', order.id);

  return (
    <div className="container">
      <button onClick={() => navigate(-1)} className="btn btn-secondary mb-2">
        ← Back
      </button>

      <div className="card">
        {/* Order Header */}
        <div className="order-header">
          <div className="flex-between">
            <h2>Order #{order.id}</h2>
            <span className={`badge badge-${ORDER_STATUS_COLORS[order.status]}`}>
              {ORDER_STATUS_LABELS[order.status]}
            </span>
          </div>
        </div>

        {/* Order Meta Information */}
        <div className="order-meta">
          <div className="meta-item">
            <span className="meta-label">Order Date</span>
            <span className="meta-value">{formatDate(order.createdAt)}</span>
          </div>
          <div className="meta-item">
            <span className="meta-label">Order Status</span>
            <span className="meta-value">{ORDER_STATUS_LABELS[order.status]}</span>
          </div>
          <div className="meta-item">
            <span className="meta-label">Total Amount</span>
            <span className="meta-value">{formatCurrency(order.totalAmount)}</span>
          </div>
        </div>

        {/* Buyer Information */}
        <div className="buyer-info">
          <h3>Buyer Details</h3>
          <div className="order-meta" style={{ background: 'white', marginTop: '15px' }}>
            <div className="meta-item">
              <span className="meta-label">Buyer ID</span>
              <span className="meta-value">{order.buyerId || "Not Provided"}</span>
            </div>
            <div className="meta-item">
              <span className="meta-label">Name</span>
              <span className="meta-value">{order.buyerName || "Not Provided"}</span>
            </div>
            <div className="meta-item">
              <span className="meta-label">Email</span>
              <span className="meta-value">{order.buyerEmail || "Not Provided"}</span>
            </div>
            <div className="meta-item">
              <span className="meta-label">Phone</span>
              <span className="meta-value">{order.buyerPhone || "Not Provided"}</span>
            </div>
            <div className="meta-item" style={{ gridColumn: '1 / -1' }}>
              <span className="meta-label">Address</span>
              <span className="meta-value">{order.buyerAddress || "Not Provided"}</span>
            </div>
          </div>
        </div>

        {/* Order Items */}
        <div className="order-items">
          <h3>Order Items</h3>
          <table className="items-table">
            <thead>
              <tr>
                <th>Product</th>
                <th>Quantity</th>
                <th>Price</th>
                <th>Subtotal</th>
              </tr>
            </thead>
            <tbody>
              {order.items.map(item => (
                <tr key={item.id}>
                  <td>{item.productName}</td>
                  <td>{item.quantity}</td>
                  <td>{formatCurrency(item.priceEach)}</td>
                  <td>{formatCurrency(item.subtotal)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Order Total */}
        <div className="order-total">
          <h3>Total: {formatCurrency(order.totalAmount)}</h3>
        </div>

        {/* Action Buttons */}
        <div className="order-actions">
          {order.status === 'PENDING' && (
            <>
              <button onClick={handleConfirm} className="btn btn-success">
                ✓ Confirm Order
              </button>
              <button onClick={handleReject} className="btn btn-danger">
                ✗ Reject Order
              </button>
            </>
          )}
          {order.status === 'CONFIRMED' && (
            <button onClick={handleComplete} className="btn btn-primary">
              ✓ Mark as Completed
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default OrderDetails;
