import React from 'react';
import './Loader.css';

const Loader = ({
  size = 50,
  color = '#25b09b',
  thickness = 8,
  speed = '1s',
  message = '',
  className = '',
  style = {}
}) => {
  const loaderStyle = {
    '--loader-size': `${size}px`,
    '--loader-color': color,
    '--loader-thickness': `${thickness}px`,
    '--loader-speed': speed,
    width: size,
    height: size,
    padding: thickness,
    ...style
  };

  return (
    <div className={`loader-container ${className}`}>
      <div
        className="loader"
        style={loaderStyle}
        aria-label="Loading"
        role="status"
      />
      {message && <p className="loader-message">{message}</p>}
    </div>
  );
};

// Also export a small version for inline use
export const SmallLoader = (props) => (
  <Loader size={30} thickness={6} {...props} />
);

// Export a large version
export const LargeLoader = (props) => (
  <Loader size={80} thickness={10} {...props} />
);

// Export for full page loading
export const FullPageLoader = ({ message = 'Loading...' }) => (
  <div className="full-page-loader">
    <Loader size={60} thickness={8} />
    {message && <p className="full-page-message">{message}</p>}
  </div>
);

export default Loader;
