import React, { useState, useEffect } from 'react';
import { orderApi } from '../../api/orderApi';
import OrderCard from '../../components/order/OrderCard';
import Loading from '../../components/common/Loading';
import ErrorMessage from '../../components/common/ErrorMessage';
import { formatCurrency } from '../../utils/helpers';

const SalesHistory = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchSalesHistory();
  }, []);

  const fetchSalesHistory = async () => {
    try {
      const response = await orderApi.getSalesHistory();
      setOrders(response.data);
    } catch (err) {
      setError('Failed to load sales history');
    } finally {
      setLoading(false);
    }
  };

  const getTotalRevenue = () => {
    return orders.reduce((total, order) => total + parseFloat(order.totalAmount), 0);
  };

  if (loading) return <Loading />;

  return (
    <div className="container">
      <h1>Sales History</h1>

      {orders.length > 0 && (
        <div className="card mb-3">
          <h3>Total Revenue from Completed Sales</h3>
          <p className="stat-value">{formatCurrency(getTotalRevenue())}</p>
        </div>
      )}

      <ErrorMessage message={error} onClose={() => setError('')} />

      {orders.length === 0 ? (
        <div className="card text-center">
          <p>No completed sales yet.</p>
        </div>
      ) : (
        <div>
          {orders.map(order => (
            <OrderCard key={order.id} order={order} userRole="FARMER" />
          ))}
        </div>
      )}
    </div>
  );
};

export default SalesHistory;
