import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { orderApi } from '../../api/orderApi';
import Loading from '../../components/common/Loading';
import ErrorMessage from '../../components/common/ErrorMessage';
import { formatCurrency } from '../../utils/helpers';
import './BuyerDashboard.css';

const BuyerDashboard = () => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const response = await orderApi.getBuyerStats();
      setStats(response.data);
    } catch (err) {
      setError('Failed to load dashboard statistics');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <Loading />;

  return (
    <div className="container">
      <h1>Buyer Dashboard</h1>

      <ErrorMessage message={error} onClose={() => setError('')} />

      <div className="stats-grid">
        <div className="stat-card">
          <h3>Total Orders</h3>
          <p className="stat-value">{stats?.totalOrders || 0}</p>
        </div>
        <div className="stat-card">
          <h3>Pending Orders</h3>
          <p className="stat-value">{stats?.pendingOrders || 0}</p>
        </div>
        <div className="stat-card">
          <h3>Completed Orders</h3>
          <p className="stat-value">{stats?.completedOrders || 0}</p>
        </div>
        <div className="stat-card">
          <h3>Total Spent</h3>
          <p className="stat-value">{formatCurrency(stats?.totalSpent || 0)}</p>
        </div>
      </div>

      <div className="quick-actions">
        <h2>Quick Actions</h2>
        <div className="actions-grid">
          <Link to="/products" className="action-card">
            <span className="action-icon">ðŸ›’</span>
            <h3>Browse Products</h3>
            <p>Explore available products from farmers</p>
          </Link>
          <Link to="/buyer/orders" className="action-card">
            <span className="action-icon">ðŸ“¦</span>
            <h3>My Orders</h3>
            <p>View and manage your orders</p>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default BuyerDashboard;
