import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { FaUser, FaLock, FaArrowRight } from 'react-icons/fa';

const LoginForm = () => {
  const [formData, setFormData] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    const result = await login(formData);
    setLoading(false);

    if (result.success) {
      const redirectPath = result.user.role === 'FARMER' ? '/farmer/dashboard' : '/buyer/dashboard';
      navigate(redirectPath);
    } else {
      setError(result.error);
    }
  };

  return (
    <div className="auth-container">
      <div className="screen">
        <div className="screen__content">
          <form className="login" onSubmit={handleSubmit}>
            <h2 className="login__title">Welcome Back</h2>

            {error && (
              <div className="error-message">
                <span className="error-text">{error}</span>
                <button
                  type="button"
                  className="error-close"
                  onClick={() => setError('')}
                >
                  Ã—
                </button>
              </div>
            )}

            <div className="login__field">
              <FaUser className="login__icon" />
              <input
                type="email"
                name="email"
                className="login__input"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="login__field">
              <FaLock className="login__icon" />
              <input
                type="password"
                name="password"
                className="login__input"
                placeholder="Password"
                value={formData.password}
                onChange={handleChange}
                required
              />
            </div>

            <button type="submit" className="login__submit" disabled={loading}>
              <span className="button__text">{loading ? 'Logging in...' : 'Log In Now'}</span>
              <FaArrowRight className="button__icon" />
            </button>

            <div className="auth-footer">
              <p className="auth-link">
                Don't have an account? <Link to="/signup" className="auth-link__text">Sign up</Link>
              </p>
            </div>
          </form>
        </div>
        <div className="screen__background">
          <span className="screen__background__shape screen__background__shape4"></span>
          <span className="screen__background__shape screen__background__shape3"></span>
          <span className="screen__background__shape screen__background__shape2"></span>
          <span className="screen__background__shape screen__background__shape1"></span>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
